    this.service.getPosts().subscribe(res => {
      this.list = res.data;
      // console.log(this.list)
    });
  }

  onTableDataChange(event) {
    this.page = event;
    this.getQuestion();
  }
  onTableSizeChange(event) {
    this.tableSize = event.target.value;
    this.page = 1;
    this.getQuestion();
  }
