    this.service.setcomment(question_id, this.bodynewqcomment[question_id], user_id).subscribe(res => {
      if (res) {
        this.bodynewqcomment[question_id] = "";
        this.refresh();
      }
    });
  }
  updateqcommentbyid(id) {
    this.service.updatecomment(id, this.bodyupdateqcomment).subscribe(res => {
      if (res) {
        this.refresh();
      }
    });
    this.visiabletextupdateqcomment('-1', "-1");
    this.bodyupdateqcomment = '';
  }
