    if (confirm('Are You Sure To Delete This question?')) {
      this.service.deletePost(question_id).subscribe(res => {
        this.refresh();
      });
    }
  }
  deleteqcommentbyid(qcomment_id) {
    if (confirm('Are You Sure To Delete This Comment?')) {
      this.service.deletecomment(qcomment_id).subscribe(res => {
        this.refresh();
      });
    }
  }
