    this.page = event;
    this.getQuestion();
  }
  onTableSizeChange(event) {
    this.tableSize = event.target.value;
    this.page = 1;
    this.getQuestion();
  }
  // to excute the question updateting in database
  updatequestionbyid(id) {
    this.service.updatequestionbyid(id, this.bodyupdatequestion, this.subtrack_id).subscribe(res => {
      if (res) {
        this.addupdatedtoquestions();
      }
    });
    this.visibleupdatequestion("-1", "-1", "-1", "-1");
  }
  // to visible question update model
