      subtrack_id: new FormControl("", [Validators.required]),
      user_id: new FormControl(this.user_logened.id, [Validators.required])
    });
  }
  savequestion() {
    console.log(this.formAddQuestion.value);
    if (this.formAddQuestion.valid) {
      console.log(this.formAddQuestion.value);
      this.service.addquestion(this.formAddQuestion.value).subscribe(res => {
        this.refresh();
        this.removeformdata();
      });
    }
  }
  // end add question
  // to get all question
  getQuestion() {
    this.service.getquestionbyuser(this.user_logened.id).subscribe(res => {
      this.list = res.data;
      console.log(res.data);
    });
  }
  onTableDataChange(event) {
    this.page = event;
    this.getQuestion();
  }
  onTableSizeChange(event) {
    this.tableSize = event.target.value;
    this.page = 1;
    this.getQuestion();
  }
  // to excute the question updateting in database
  updatequestionbyid(id) {
    this.service.updatequestionbyid(id, this.bodyupdatequestion, this.subtrack_id).subscribe(res => {
      if (res) {
        this.addupdatedtoquestions();
      }
    });
    this.visibleupdatequestion("-1", "-1", "-1", "-1");
  }
  // to visible question update model
  visibleupdatequestion(question_id, subtrackname, subtrackid, body) {
    this.bodyupdatequestion = body;
